mapper.select:

select 

from device_dataflow
where device_id = #{deviceId,jdbcType=INTEGER}
  and type = #{type,jdbcType=VARCHAR}
  and privilege_id = #{privilegeId,jdbcType=INTEGER}
  and src = #{src,jdbcType=VARCHAR}
  and sport = #{sport,jdbcType=VARCHAR}
  and dst = #{dst,jdbcType=VARCHAR}
  and dport = #{dport,jdbcType=VARCHAR}
  and protocol = #{protocol,jdbcType=VARCHAR}
  and variant_id = #{variantId,jdbcType=INTEGER}

mapper.delete:

delete from device_dataflow
where device_id = #{deviceId,jdbcType=INTEGER}
  and type = #{type,jdbcType=VARCHAR}
  and privilege_id = #{privilegeId,jdbcType=INTEGER}
  and src = #{src,jdbcType=VARCHAR}
  and sport = #{sport,jdbcType=VARCHAR}
  and dst = #{dst,jdbcType=VARCHAR}
  and dport = #{dport,jdbcType=VARCHAR}
  and protocol = #{protocol,jdbcType=VARCHAR}
  and variant_id = #{variantId,jdbcType=INTEGER}

mapper.update:

update device_dataflow

  
    t_privilege_id = #{tPrivilegeId,jdbcType=INTEGER},
  
  
    t_src = #{tSrc,jdbcType=VARCHAR},
  
  
    t_sport = #{tSport,jdbcType=VARCHAR},
  
  
    t_dst = #{tDst,jdbcType=VARCHAR},
  
  
    t_dport = #{tDport,jdbcType=VARCHAR},
  
  
    t_protocol = #{tProtocol,jdbcType=VARCHAR},
  
  
    t_variant_id = #{tVariantId,jdbcType=INTEGER},
  
  
    priority = #{priority,jdbcType=INTEGER},
  

where device_id = #{deviceId,jdbcType=INTEGER}
  and type = #{type,jdbcType=VARCHAR}
  and privilege_id = #{privilegeId,jdbcType=INTEGER}
  and src = #{src,jdbcType=VARCHAR}
  and sport = #{sport,jdbcType=VARCHAR}
  and dst = #{dst,jdbcType=VARCHAR}
  and dport = #{dport,jdbcType=VARCHAR}
  and protocol = #{protocol,jdbcType=VARCHAR}
  and variant_id = #{variantId,jdbcType=INTEGER}


update device_dataflow
set t_privilege_id = #{tPrivilegeId,jdbcType=INTEGER},
  t_src = #{tSrc,jdbcType=VARCHAR},
  t_sport = #{tSport,jdbcType=VARCHAR},
  t_dst = #{tDst,jdbcType=VARCHAR},
  t_dport = #{tDport,jdbcType=VARCHAR},
  t_protocol = #{tProtocol,jdbcType=VARCHAR},
  t_variant_id = #{tVariantId,jdbcType=INTEGER},
  priority = #{priority,jdbcType=INTEGER}
where device_id = #{deviceId,jdbcType=INTEGER}
  and type = #{type,jdbcType=VARCHAR}
  and privilege_id = #{privilegeId,jdbcType=INTEGER}
  and src = #{src,jdbcType=VARCHAR}
  and sport = #{sport,jdbcType=VARCHAR}
  and dst = #{dst,jdbcType=VARCHAR}
  and dport = #{dport,jdbcType=VARCHAR}
  and protocol = #{protocol,jdbcType=VARCHAR}
  and variant_id = #{variantId,jdbcType=INTEGER}
